import {
  Body,
  ConflictException,
  Controller,
  Get,
  NotFoundException,
  Param,
  Post,
  Res,
  // StreamableFile,
  UseGuards,
  Logger,
} from '@nestjs/common';
import { AuthGuard } from '@nestjs/passport';
import * as fs from 'fs';
import * as path from 'path';
import { CreateFilesDto } from '../dto/CreateFilesDto';
import { v4 as uuidv4 } from 'uuid';

@Controller('files')
@UseGuards(AuthGuard('jwt'))
export class FilesController {
  logger = new Logger();

  @Post()
  async uploadFiles(@Body() files: CreateFilesDto) {
    const FILES_URL = process.env.FILES_URL;

    try {
      const urlFiles: string[] = [];

      files.files.forEach((file) => {
        const base64Img = file.base64;
        const fileName = `${uuidv4()}.${file.extension}`;
        const content = Buffer.from(base64Img, 'base64');
        fs.writeFileSync(`./public/${fileName}`, content);

        urlFiles.push(`${FILES_URL}/${fileName}`);

        this.logger.log(
          `Save file ${fileName}`,
          `${FilesController.name} | ${this.uploadFiles.name}`,
        );
      });

      this.logger.log(
        `Save file END`,
        `${FilesController.name} | ${this.uploadFiles.name}`,
      );

      return {
        urlFiles,
      };
    } catch (error) {
      this.logger.error(
        `Error[${error.message}]`,
        error,
        `${FilesController.name} | ${this.uploadFiles.name}`,
      );
      throw new ConflictException('Error loading file');
    }
  }

  @Get(':fileName')
  getFile(@Res() res, @Param('fileName') fileName: string) {
    const path_ = path.join(__dirname, `../../../public/${fileName}`);

    if (!fs.existsSync(path_)) {
      throw new NotFoundException('File not found');
    }

    res.sendFile(path_);
  }
}

